import {UserType} from "../HW8";

export type homeWorkReducerType = sortUpACType | sortDownACType

export type sortUpACType = ReturnType<typeof sortUpAC>
export type sortDownACType = ReturnType<typeof sortDownAC>


export const homeWorkReducer = (state: Array<UserType>, action: homeWorkReducerType): Array<UserType> => {
    switch (action.type) {
        case 'sort': {
            return state.sort(function(a, b){
                let nameA = a.name.toLowerCase(), nameB = b.name.toLowerCase();
                if (nameA < nameB) //sort string ascending
                    return -1;
                if (nameA > nameB)
                    return 1;
                return 0; //default return value (no sorting)
            });
        }
        case 'sort': {
            return state.sort(function(a, b){
                let nameA = a.name.toLowerCase(), nameB = b.name.toLowerCase();
                if (nameA < nameB) //sort string ascending
                    return 1;
                if (nameA > nameB)
                    return -1;
                return 0; //default return value (no sorting)
            });
        }
        case 'check': {
            // need to fix
            // @ts-ignore
            return state.sort(({age:a}, {age:b}) => a-b)
        }
        default: return state
    }
}

export const sortUpAC = () => {
    return {
        type: 'sort',
        payload: 'up'
    } as const
}

export const sortDownAC = () => {
    return {
        type: 'sort',
        payload: 'down'
    } as const
}
