export const homeWorkReducer = (state: any, action: any): any => { // need to fix any
    switch (action.type) {
        case 'sort': {
            if (action === "down"){
                // @ts-ignore
                return state.sort(({age:a}, {age:b}) => a-b)
            }
            if (action === "up") {
                // @ts-ignore
                return state.sort(({age:a}, {age:b}) => a-b)
            }

        }
        case 'check': {
            // need to fix
            // @ts-ignore
            return state.sort(({age:a}, {age:b}) => a-b)
        }
        default: return state
    }
}

export const sortAC = () => {
    return {
        type: 'sort',
        payload: ''
    }
}
