import {UserType} from "../HW8";

export type homeWorkReducerType = sortACType

export type sortACType = ReturnType<typeof sortUpAC>

export const homeWorkReducer = (state: Array<UserType>, action: any): any => { // need to fix any
    switch (action.type) {
        case 'sort': {
            // need to fix
            // @ts-ignore
            return state.sort(({age:a}, {age:b}) => a-b)
        }
        case 'sort': {
            // need to fix
            // @ts-ignore
            return state.sort(({age:a}, {age:b}) => b-a)
        }
        case 'check': {
            // need to fix
            // @ts-ignore
            return state.sort(({age:a}, {age:b}) => a-b)
        }
        default: return state
    }
}

export const sortUpAC = () => {
    return {
        type: 'sort',
        payload: 'up'
    } as const
}

export const sortDownAC = () => {
    return {
        type: 'sort',
        payload: 'down'
    } as const
}
